import React, { useState } from 'react'
import { 
  Text,
  StyleSheet,
  View,
  TextInput,
  TouchableOpacity,
  Image,
} from 'react-native'

export default App = () => {

  const [percentage, setPercentage] = useState(0);
  const [name1, changeTextName1] = useState('');
  const [name2, changeTextName2] = useState('');

  function calculatePercentage() {
    fetch(`https://love-calculator.p.rapidapi.com/getPercentage?fname=${name1}&sname=${name2}`, {
      "method": "GET",
      "headers": {
        "x-rapidapi-key": "50a6ef8643msh531ca5ca3434591p17b1fcjsn56f86d5f0c88",
        "x-rapidapi-host": "love-calculator.p.rapidapi.com"
      }
    })
    .then(response => response.json()) 
    .then(response => {
      setPercentage(percentage => percentage = response["percentage"]);
    })
    .catch(error => {
      console.log(error);
    })
  }

  return (
    <View style={styles.container}>
      <Image
        style={styles.image}
        source={require('../assets/love.jpg')}
      />

      <TextInput 
        style={styles.input}
        placeholder='Enter first name'
        onChangeText={text => changeTextName1(text)}
        value={name1}
      />

      <TextInput 
        style={styles.input}
        placeholder='Enter second name'
        onChangeText={text => changeTextName2(text)}
        value={name2}
      />

      <TouchableOpacity
        style={styles.button}
        onPress={calculatePercentage}
      >
        <Text style={styles.textButton}>Calculate</Text>
      </TouchableOpacity>

      <Text style={styles.text}>Percentage: {percentage}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent:'center',
    alignItems: 'center',
    backgroundColor: '#107a9e',
  },
  input: {
    height: 50,
    width: 300,
    marginBottom: 20,
    backgroundColor: '#b5b5b5',
    borderRadius: 5,
    padding: 5
  },
  text: {
    marginTop: 20,
    color: '#fff',
    fontSize: 40,
    fontWeight: 'bold'
  },
  button: {
    height: 60,
    width: 200,
    backgroundColor: '#135b96',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 5,
    borderRadius: 5,

  },
  textButton: {
    fontSize: 17,
    color: '#fff',
  },
  image: {
    height: 300,
    width: 300,
    borderRadius: 300,
    marginBottom: 20
  }
});
